mapper.select:
SELECT 
FROM appointments
WHERE customer_id = #{customerId}
ORDER BY appointment_date DESC, appointment_time DESC
LIMIT #{limit} OFFSET #{offset}

SELECT 
FROM appointments
WHERE customer_id = #{customerId} AND status = #{status}
ORDER BY appointment_date DESC, appointment_time DESC
LIMIT #{limit} OFFSET #{offset}

SELECT 
FROM appointments
WHERE customer_id = #{customerId}
ORDER BY appointment_date DESC, appointment_time DESC
LIMIT #{limit}

SELECT COUNT(*)
FROM appointments
WHERE customer_id = #{customerId}

SELECT COUNT(*)
FROM appointments
WHERE customer_id = #{customerId} AND status = #{status}

SELECT COUNT(*)
FROM appointments
WHERE tenant_id = #{tenantId}

mapper.insert:
INSERT INTO appointments (
    tenant_id, customer_id, staff_id, appointment_date, appointment_time,
    duration, total_amount, status, notes, rating, review, created_at, updated_at
) VALUES (
    #{tenantId}, #{customerId}, #{staffId}, #{appointmentDate}, #{appointmentTime},
    #{duration}, #{totalAmount}, #{status}, #{notes}, #{rating}, #{review}, #{createdAt}, #{updatedAt}
)
