mapper.select:
SELECT COUNT(*) = 0 FROM appointments a
WHERE a.resource_id = #{resourceId}
AND a.appointment_date = #{date}
AND a.status NOT IN ('CANCELLED', 'NO_SHOW')
AND (
    (#{startTime} >= a.appointment_time AND #{startTime} < ADDTIME(a.appointment_time, SEC_TO_TIME(a.duration * 60))) OR
    (#{endTime} > a.appointment_time AND #{endTime} <= ADDTIME(a.appointment_time, SEC_TO_TIME(a.duration * 60))) OR
    (#{startTime} <= a.appointment_time AND #{endTime} >= ADDTIME(a.appointment_time, SEC_TO_TIME(a.duration * 60)))
)

mapper.insert:
INSERT INTO resource (
    tenant_id, name, type, description, capacity, location, 
    equipment, specialties, hourly_rate, status, phone, email, position, start_date, created_at, updated_at
) VALUES (
    #{tenantId}, #{name}, #{type}, #{description}, #{capacity}, #{location},
    #{equipment}, #{specialties}, #{hourlyRate}, #{status}, #{phone}, #{email}, #{position}, #{startDate}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT * FROM resource_availability 
WHERE resource_id = #{resourceId}
ORDER BY day_of_week, start_time

mapper.insert:
INSERT INTO resource_availability (
    resource_id, day_of_week, start_time, end_time, is_available, created_at, updated_at
) VALUES (
    #{resourceId}, #{dayOfWeek}, #{startTime}, #{endTime}, #{isAvailable}, #{createdAt}, #{updatedAt}
)
